import React from "react";
import AnimationRevealPage from "helpers/AnimationRevealPage.js";
import tw from "twin.macro";
import styled from "styled-components"; //eslint-disable-line
import MainFeature1 from "components/features/ServerCard.js";

import axios from "axios";

const statusCheckerUrl = "http://localhost:1234/";
const serverList = [['The Island',27015],["Scortched Earth",27016]]

const Subheading = tw.span`uppercase tracking-wider text-sm`;
export default () => {
    const [post, setPost] = React.useState(null);

    var fullUrl = statusCheckerUrl;
    for (var t = 0; t < serverList.length; t++) {
        if (t==0) fullUrl += "?";
        else fullUrl += "&";
        fullUrl += "server"+t+"=";
        fullUrl += serverList[t][1];
    }
    console.log(fullUrl);

    React.useEffect(() => {
        axios.get(fullUrl).then((response) => {
            console.log(response.data)
            setPost(response.data);
        });
    }, []);

  return (
    <AnimationRevealPage>
        {/*Server as a foreach*/}
        {serverList.map((server, somethignUnsuded) => {
            if(!post)
                 return (<MainFeature1
                    subheading={<Subheading>{server[0]}</Subheading>}
                    heading="Waiting on response from Status-Checker...If this doesn't go away within 10 seconds, pls message Ian urgently!"
                    description=""
                    buttonRounded={false}
                    primaryButtonText="Message Ian"
                    primaryButtonUrl="https://discordapp.com/users/178233738109517824/"
                    imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                />);
                else
                if (post[somethignUnsuded].serverName == null)
                     return (<MainFeature1
                        subheading={<Subheading>{server[0]}</Subheading>}
                        heading={"Ark Server is offline"}
                        description={post[somethignUnsuded]}
                        description2={"Please Message Ian about this!"}
                        buttonRounded={false}
                        primaryButtonText="Message Ian"
                        primaryButtonUrl="https://discordapp.com/users/178233738109517824/"
                        imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                    />);
                    else return (<MainFeature1
                        subheading={<Subheading>{server[0]}</Subheading>}
                        heading={"Ark Server: "+post[somethignUnsuded].serverName+" is online!"}
                        description={"Game being played = "+post.gameDescription}
                        description2={"on Map = "+post.map}
                        description3={post[somethignUnsuded].numberOfPlayers+" / "+post[somethignUnsuded].maxNumberOfPlayers+" player"}
                        buttonRounded={false}
                        primaryButtonText="Join the Server!"
                        primaryButtonUrl={post[somethignUnsuded].connectUrl+"123/"}
                        imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                    />);
        })}

        {/*Server on pos 0*/}
        {!post
        ? <MainFeature1
                subheading={<Subheading>Ark The Island</Subheading>}
                heading="Waiting on response from Status-Checker...If this doesn't go away within 10 seconds, pls message Ian urgently!"
                description=""
                buttonRounded={false}
                primaryButtonText="Message Ian"
                primaryButtonUrl="https://discordapp.com/users/178233738109517824/"
                imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
            />
        :
            post[0].serverName == null
                ? <MainFeature1
                    subheading={<Subheading>Ark The Island</Subheading>}
                    heading={"Ark Server is offline"}
                    description={post[0]}
                    description2={"Please Message Ian about this!"}
                    buttonRounded={false}
                    primaryButtonText="Message Ian"
                    primaryButtonUrl="https://discordapp.com/users/178233738109517824/"
                    imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                />
                    : <MainFeature1
                    subheading={<Subheading>Ark The Island</Subheading>}
                    heading={"Ark Server: "+post[0].serverName+" is online!"}
                    description={"Game being played = "+post.gameDescription}
                    description2={"on Map = "+post.map}
                    description3={post[0].numberOfPlayers+" / "+post[0].maxNumberOfPlayers+" player"}
                    buttonRounded={false}
                    primaryButtonText="Join the Server!"
                    primaryButtonUrl={post[0].connectUrl+"123/"}
                    imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                />
            }

        {/*Server on pos 1*/}
        {!post
            ? <MainFeature1
                subheading={<Subheading>Server 1</Subheading>}
                heading="Waiting on response from Status-Checker...If this doesn't go away within 10 seconds, pls message Ian urgently!"
                description=""
                buttonRounded={false}
                primaryButtonText="Message Ian"
                primaryButtonUrl="https://discordapp.com/users/178233738109517824/"
                imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
            />
            :
            post[1].serverName == null
                ? <MainFeature1
                    subheading={<Subheading>Server 1</Subheading>}
                    heading={"Ark Server is offline"}
                    description={post[0]}
                    description2={"Please Message Ian about this!"}
                    buttonRounded={false}
                    primaryButtonText="Message Ian"
                    primaryButtonUrl="https://discordapp.com/users/178233738109517824/"
                    imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                />
                : <MainFeature1
                    subheading={<Subheading>Server 1</Subheading>}
                    heading={"Ark Server: "+post[1].serverName+" is online!"}
                    description={"Game being played = "+post.gameDescription}
                    description2={"on Map = "+post.map}
                    description3={post[1].numberOfPlayers+" / "+post[1].maxNumberOfPlayers+" player"}
                    buttonRounded={false}
                    primaryButtonText="Join the Server!"
                    primaryButtonUrl={post[1].connectUrl+"123/"}
                    imageSrc="https://ark.gamepedia.com/media/ark.gamepedia.com/thumb/0/04/The_Island_Topographic_Map.jpg/600px-The_Island_Topographic_Map.jpg"
                />
        }


    </AnimationRevealPage>
  );
};

